mapper.select:
SELECT REAGENT_SQ , USER_EMAIL , CATEGORY_CD , PRODUCT_NAME , CAT_NO , CAS_NO , VENDOR_CD , CAPACITY , LOCATION_SQ , REG_DT , USE_DT , EXPIRE_DT , STATUS FROM LOGIS_REAGENT WHERE USER_EMAIL = #{userEmail}
AND LEFT(REPLACE(#{fromDt}, '-', ''), 8) >= LEFT(LEFT(REPLACE(REG_DT, '-', ''), 8)) AND LEFT(REPLACE(#{toDt}, '-', ''), 8) <= LEFT(LEFT(REPLACE(REG_DT, '-', ''), 8))
AND LEFT(REPLACE(#{fromDt}, '-', ''), 8) >= LEFT(LEFT(REPLACE(USE_DT, '-', ''), 8)) AND LEFT(REPLACE(#{toDt}, '-', ''), 8) <= LEFT(LEFT(REPLACE(USE_DT, '-', ''), 8))
AND LEFT(REPLACE(#{fromDt}, '-', ''), 8) >= LEFT(LEFT(REPLACE(EXPIRE_DT, '-', ''), 8)) AND LEFT(REPLACE(#{toDt}, '-', ''), 8) <= LEFT(LEFT(REPLACE(EXPIRE_DT, '-', ''), 8))
AND PRODUCT_NAME LIKE CONCAT('%', #{search}, '%') OR CAT_NO LIKE CONCAT('%', #{search}, '%') OR CAS_NO LIKE CONCAT('%', #{search}, '%') OR VENDOR_CD LIKE CONCAT('%', #{search}, '%') OR CAPACITY LIKE CONCAT('%', #{search}, '%') OR LOCATION_SQ LIKE CONCAT('%', #{search}, '%') OR REG_DT LIKE CONCAT('%', #{search}, '%') OR USE_DT LIKE CONCAT('%', #{search}, '%') OR EXPIRE_DT LIKE CONCAT('%', #{search}, '%')
LIMIT #{pageOffset}, #{pageCount};
